(4, 'null', 'Không chấp hành hiệu lệnh', ', Khong chap hanh hieu lenh, chap hanh hieu lenh, khong chap hanh hieu lenh', 103653, 47955, '', 'Phạt tiền từ 100.000 đồng đến 200.000 đồng', 0, 0, '', '', '', 0, 0, 0, '', '2015-02-10 14:22:41'),
(6, 'null', 'Vi phạm quy định về Giấy chứng nhận đăng ký doanh nghiệp', ', Vi pham quy dinh ve Giay chung nhan dang ky doanh nghiep, giay phep, Giay chung nhan, dang ky, doanh nghiep, Vi pham, quy dinh, dau tu, Van phong dai dien , Chi nhanh', 115267, 435259, '', 'từ 1.000.000 đồng đến 3.000.000 đồng', 0, 0, '', '', '', 0, 0, 0, '', '2015-02-10 14:22:41'),
(7, 'null', 'Kinh doanh không đúng ngành nghề ghi trong Giấy phép', ', Kinh doanh khong dung nganh nghe ghi trong Giay phep, kinh doanh, giay phep, Giay chung nhan, dang ky, doanh nghiep, khong dung nganh nghe, Giay phep dau tu, dau tu, Van phong dai dien , Chi nhanh, nganh nghe', 115267, 435260, '', 'từ 1.000.000 đồng đến 3.000.000 đồng', 0, 0, '', '', '', 0, 0, 0, '', '2015-02-10 14:22:41'),
(8, 'null', 'Kinh doanh không đúng mặt hàng ghi trong Giấy phép', ', Kinh doanh khong dung mat hang ghi trong Giay phep, giay phep, Giay chung nhan, dang ky, doanh nghiep, Giay phep dau tu, dau tu, hoat dong Chi nhanh, Van phong dai dien , Chi nhanh, khong dung mat hang', 115267, 435260, '', 'từ 1.000.000 đồng đến 3.000.000 đồng', 0, 0, '', '', '', 0, 0, 0, '', '2015-02-10 14:22:41'),
(9, 'null', 'Kinh doanh không đúng địa điểm ghi trong Giấy phép', ', Kinh doanh khong dung dia diem ghi trong Giay phep, kinh doanh, giay phep, Giay chung nhan, Giay phep dau tu, dau tu, hoat dong Chi nhanh, Van phong dai dien , Chi nhanh, khong dung dia diem, dia diem kinh doanh ', 115267, 435260, '', 'từ 1.000.000 đồng đến 3.000.000 đồng', 0, 0, '', '', '', 0, 0, 0, '', '2015-02-10 14:22:41'),
